mapper.select:
SELECT M.member_name, C1.category_name AS upper_division, C2.category_name AS lower_division, M.ad_account, M.office_phone_number, M.is_served FROM member M JOIN category C1 ON (C1.category_code = M.upper_division) JOIN category C2 ON (C2.category_code = M.lower_division) JOIN authorities A ON (M.ad_account = A.username AND A.authority != 'ROLE_CREATER') WHERE 1=1
AND C1.category_name = #{upperCategory}
AND C2.category_name = #{lowerCategory}
AND M.ad_account LIKE '%${adAccount}%'
AND M.member_name LIKE '%${nameOfMember}%'
LIMIT #{page}, #{viewSolePage}

mapper.update:
UPDATE
	member
SET
	member_name = #{memberName}, upper_division = #{upperDivisionCode}, lower_division = #{lowerDivisionCode}, ad_account = #{adAccount}, office_phone_number = #{officePhoneNumber}
WHERE
	ad_account = #{adAccount}

mapper.select:
SELECT M.member_name, C1.category_name AS upper_division, C2.category_name AS lower_division, M.ad_account, M.office_phone_number FROM member M JOIN category C1 ON (C1.category_code = M.upper_division) JOIN category C2 ON (C2.category_code = M.lower_division) WHERE is_served = 'N'
AND C1.category_name = #{upperCategory}
AND C2.category_name = #{lowerCategory}
AND M.ad_account LIKE '%${adAccount}%'
AND M.member_name LIKE '%${nameOfMember}%'
ORDER BY ad_account DESC LIMIT #{page}, #{viewSolePage}

SELECT COUNT(ad_account) FROM member M JOIN category C1 ON (C1.category_code = M.upper_division) JOIN category C2 ON (C2.category_code = M.lower_division) WHERE is_served = 'Y'
AND C1.category_name = #{upperCategory}
AND C2.category_name = #{lowerCategory}
AND M.ad_account LIKE '%${adAccount}%'
AND M.member_name LIKE '%${nameOfMember}%'

SELECT COUNT(ad_account) FROM member M JOIN category C1 ON (C1.category_code = M.upper_division) JOIN category C2 ON (C2.category_code = M.lower_division) WHERE is_served = 'N'
AND C1.category_name = #{upperCategory}
AND C2.category_name = #{lowerCategory}
AND M.ad_account LIKE '%${adAccount}%'
AND M.member_name LIKE '%${nameOfMember}%'
